package syntaxclass02;

public class HomeWorkk {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing whole number values;
        byte box1=127;
        short box2=30123;
        int box3=409877657;
        long box4=1234567891;

        //storing and representing decimal number values;
        float variable1=0.59F;
        double variable2=33.98;

        //storing and representing single characters;
        char container='g';
        char percentage='%';

        //storing and representing boolean;
        boolean high=true;
        boolean low=false;

        System.out.println(box1);
        System.out.println(box2);
        System.out.println(box3);
        System.out.println(box4);

        System.out.println(variable1);
        System.out.println(variable2);

        System.out.println(container);
        System.out.println(percentage);

        System.out.println(high);
        System.out.println(low);
        System.out.println("low"); //printing text variable;
	}

}
